// Component shared functionality
uint16 CarrierComponent::GetOwningEntityId() const
{
	return this - &ArgusComponentRegistry::s_CarrierComponents[0];
}

void CarrierComponent::Reset()
{
	m_passengerEntityIds.Reset();
	m_carrierCapacity = 0u;
}

void CarrierComponent::DrawComponentDebug() const
{
#if !UE_BUILD_SHIPPING
	if (!ImGui::CollapsingHeader("CarrierComponent"))
	{
		return;
	}

	if (ImGui::BeginTable("ComponentValues", 2, ImGuiTableFlags_NoSavedSettings | ImGuiTableFlags_BordersInner))
	{
		ImGui::TableNextColumn();
		ImGui::Text("m_passengerEntityIds");
		ImGui::TableNextColumn();
		ImGui::Text("Array max is currently = %d", m_passengerEntityIds.Max());
		if (m_passengerEntityIds.Num() == 0)
		{
			ImGui::Text("Array is empty");
		}
		else
		{
			ImGui::Text("Size of array = %d", m_passengerEntityIds.Num());
			ImGui::Indent();
			for (int32 i = 0; i < m_passengerEntityIds.Num(); ++i)
			{
				if (i != 0) ImGui::Separator();
				ImGui::Text("%d", m_passengerEntityIds[i]);
			}
			ImGui::Unindent();
		}
		ImGui::TableNextColumn();
		ImGui::Text("m_carrierCapacity");
		ImGui::TableNextColumn();
		ImGui::Text("%d", m_carrierCapacity);
		ImGui::EndTable();
	}
#endif //!UE_BUILD_SHIPPING
}

