// Component shared functionality
uint16 DecalComponent::GetOwningEntityId() const
{
	return this - &ArgusComponentRegistry::s_DecalComponents[0];
}

void DecalComponent::Reset()
{
	m_lifetimeSeconds = 1.0f;
	m_referencingEntityCount = 0u;
	m_connectedEntityId = ArgusECSConstants::k_maxEntities;
	m_lifetimeTimer.Reset();
}

void DecalComponent::DrawComponentDebug() const
{
#if !UE_BUILD_SHIPPING
	if (!ImGui::CollapsingHeader("DecalComponent"))
	{
		return;
	}

	if (ImGui::BeginTable("ComponentValues", 2, ImGuiTableFlags_NoSavedSettings))
	{
		ImGui::TableNextColumn();
		ImGui::Text("m_lifetimeSeconds");
		ImGui::TableNextColumn();
		ImGui::Text("%.2f", m_lifetimeSeconds);
		ImGui::TableNextColumn();
		ImGui::Text("m_referencingEntityCount");
		ImGui::TableNextColumn();
		ImGui::Text("%d", m_referencingEntityCount);
		ImGui::TableNextColumn();
		ImGui::Text("m_connectedEntityId");
		ImGui::TableNextColumn();
		ImGui::Text("%d", m_connectedEntityId);
		ImGui::TableNextColumn();
		ImGui::Text("m_lifetimeTimer");
		ImGui::TableNextColumn();
		const ArgusEntity owningEntity = ArgusEntity::RetrieveEntity(GetOwningEntityId());
		if (m_lifetimeTimer.IsTimerTicking(owningEntity))
		{
			ImGui::Text("%.2f", m_lifetimeTimer.GetTimeRemaining(owningEntity));
			ImGui::SameLine();
			ImGui::ProgressBar(m_lifetimeTimer.GetTimeElapsedProportion(owningEntity));
		}
		else if (m_lifetimeTimer.IsTimerComplete(owningEntity))
		{
			ImGui::Text("Timer complete");
		}
		else
		{
			ImGui::Text("Not set");
		}
		ImGui::EndTable();
	}
#endif //!UE_BUILD_SHIPPING
}

