uint16 IdentityComponent::GetOwningEntityId() const
{
	return this - &ArgusComponentRegistry::s_IdentityComponents[0];
}

void IdentityComponent::DrawComponentDebug() const
{
#if !UE_BUILD_SHIPPING
	if (!ImGui::CollapsingHeader("IdentityComponent"))
	{
		return;
	}

	if (ImGui::BeginTable("ComponentValues", 2, ImGuiTableFlags_NoSavedSettings))
	{
		ImGui::TableNextColumn();
		ImGui::Text("m_factionId");
		ImGui::TableNextColumn();
		ImGui::Text("%d", m_factionId);
		ImGui::TableNextColumn();
		ImGui::Text("m_allies");
		ImGui::TableNextColumn();
		ImGui::Text("%d", m_allies);
		ImGui::TableNextColumn();
		ImGui::Text("m_enemies");
		ImGui::TableNextColumn();
		ImGui::Text("%d", m_enemies);
		ImGui::EndTable();
	}
#endif //!UE_BUILD_SHIPPING
}

