// Component shared functionality
uint16 TimerComponent::GetOwningEntityId() const
{
	return this - &ArgusComponentRegistry::s_TimerComponents[0];
}

void TimerComponent::Reset()
{
	m_timers.Reset();
}

void TimerComponent::DrawComponentDebug() const
{
#if !UE_BUILD_SHIPPING
	if (!ImGui::CollapsingHeader("TimerComponent"))
	{
		return;
	}

	if (ImGui::BeginTable("ComponentValues", 2, ImGuiTableFlags_NoSavedSettings))
	{
		ImGui::TableNextColumn();
		ImGui::Text("m_timers");
		ImGui::TableNextColumn();
		ImGui::Text("Array max is currently = %d", m_timers.Max());
		if (m_timers.Num() == 0)
		{
			ImGui::Text("Array is empty");
		}
		else
		{
			ImGui::Text("Size of array = %d", m_timers.Num());
			for (int32 i = 0; i < m_timers.Num(); ++i)
			{
			}
		}
		ImGui::EndTable();
	}
#endif //!UE_BUILD_SHIPPING
}

