// Component shared functionality
uint16 VelocityComponent::GetOwningEntityId() const
{
	return this - &ArgusComponentRegistry::s_VelocityComponents[0];
}

void VelocityComponent::Reset()
{
	m_currentVelocity = FVector2D::ZeroVector;
	m_proposedAvoidanceVelocity = FVector2D::ZeroVector;
	m_desiredSpeedUnitsPerSecond = 100.0f;
	m_desiredFlightSpeedUnitsPerSecond = 100.0f;
}

void VelocityComponent::DrawComponentDebug() const
{
#if !UE_BUILD_SHIPPING
	if (!ImGui::CollapsingHeader("VelocityComponent"))
	{
		return;
	}

	if (ImGui::BeginTable("ComponentValues", 2, ImGuiTableFlags_NoSavedSettings))
	{
		ImGui::TableNextColumn();
		ImGui::Text("m_currentVelocity");
		ImGui::TableNextColumn();
		ImGui::Text("(%.2f, %.2f)", m_currentVelocity.X, m_currentVelocity.Y);
		ImGui::NewLine();
		ImGui::TableNextColumn();
		ImGui::Text("m_proposedAvoidanceVelocity");
		ImGui::TableNextColumn();
		ImGui::Text("(%.2f, %.2f)", m_proposedAvoidanceVelocity.X, m_proposedAvoidanceVelocity.Y);
		ImGui::NewLine();
		ImGui::TableNextColumn();
		ImGui::Text("m_desiredSpeedUnitsPerSecond");
		ImGui::TableNextColumn();
		ImGui::Text("%.2f", m_desiredSpeedUnitsPerSecond);
		ImGui::NewLine();
		ImGui::TableNextColumn();
		ImGui::Text("m_desiredFlightSpeedUnitsPerSecond");
		ImGui::TableNextColumn();
		ImGui::Text("%.2f", m_desiredFlightSpeedUnitsPerSecond);
		ImGui::NewLine();
		ImGui::EndTable();
	}
#endif //!UE_BUILD_SHIPPING
}

