class ICombatComponentObserver
{
public:
};

class CombatComponentObservers
{
private:
	TArray<ICombatComponentObserver*, ArgusContainerAllocator<2> > m_CombatComponentObservers;

public:
	void AddObserver(ICombatComponentObserver* observer)
	{
		if (!observer)
		{
			return;
		}

		m_CombatComponentObservers.Add(observer);
	}

	void RemoveObserver(ICombatComponentObserver* observer)
	{
		if (!observer)
		{
			return;
		}

		m_CombatComponentObservers.RemoveAll
		(
			[observer] (ICombatComponentObserver* other)
			{
				return other == observer;
			}
		);
	}

private:

	friend struct CombatComponent;
};
