class ConstructionComponentObservers
{
private:
	TArray<IConstructionComponentObserver*, ArgusContainerAllocator<2> > m_ConstructionComponentObservers;

public:
	void AddObserver(IConstructionComponentObserver* observer)
	{
		if (!observer)
		{
			return;
		}

		m_ConstructionComponentObservers.Add(observer);
	}

	void RemoveObserver(IConstructionComponentObserver* observer)
	{
		if (!observer)
		{
			return;
		}

		m_ConstructionComponentObservers.RemoveAll
		(
			[observer] (IConstructionComponentObserver* other)
			{
				return other == observer;
			}
		);
	}

private:

	friend struct ConstructionComponent;
};
