class IPassengerComponentObserver
{
public:
	virtual void OnChanged_m_carrierEntityId(uint16 oldValue, uint16 newValue) = 0;
};

class PassengerComponentObservers
{
private:
	TArray<IPassengerComponentObserver*, ArgusContainerAllocator<2> > m_PassengerComponentObservers;

public:
	void AddObserver(IPassengerComponentObserver* observer)
	{
		if (!observer)
		{
			return;
		}

		m_PassengerComponentObservers.Add(observer);
	}

	void RemoveObserver(IPassengerComponentObserver* observer)
	{
		if (!observer)
		{
			return;
		}

		m_PassengerComponentObservers.RemoveAll
		(
			[observer] (IPassengerComponentObserver* other)
			{
				return other == observer;
			}
		);
	}

	void Reset()
	{
		m_PassengerComponentObservers.Reset();
	}

private:
	void OnChanged_m_carrierEntityId(uint16 oldValue, uint16 newValue)
	{
		for (int32 i = 0; i < m_PassengerComponentObservers.Num(); ++i)
		{
			m_PassengerComponentObservers[i]->OnChanged_m_carrierEntityId(oldValue, newValue);
		}
	};

	friend struct PassengerComponent;
};
