class IResourceComponentObserver
{
public:
};

class ResourceComponentObservers
{
private:
	TArray<IResourceComponentObserver*, ArgusContainerAllocator<2> > m_ResourceComponentObservers;

public:
	void AddObserver(IResourceComponentObserver* observer)
	{
		if (!observer)
		{
			return;
		}

		m_ResourceComponentObservers.Add(observer);
	}

	void RemoveObserver(IResourceComponentObserver* observer)
	{
		if (!observer)
		{
			return;
		}

		m_ResourceComponentObservers.RemoveAll
		(
			[observer] (IResourceComponentObserver* other)
			{
				return other == observer;
			}
		);
	}

	void Reset()
	{
		m_ResourceComponentObservers.Reset();
	}

private:

	friend struct ResourceComponent;
};
