#pragma once

#include "CoreMinimal.h"
#include "ComponentDependencies/TaskComponentStates.h"

class ISpawningComponentObserver
{
public:
};

class SpawningComponentObservers
{
private:
	TArray<ISpawningComponentObserver*> m_SpawningComponentObservers;

public:
	void AddObserver(ISpawningComponentObserver* observer)
	{
		if (!observer)
		{
			return;
		}

		m_SpawningComponentObservers.Add(observer);
	}

	void RemoveObserver(ISpawningComponentObserver* observer)
	{
		if (!observer)
		{
			return;
		}

		m_SpawningComponentObservers.RemoveAll
		(
			[observer] (ISpawningComponentObserver* other)
			{
				return other == observer;
			}
		);
	}

private:

	friend struct SpawningComponent;
};
