#include "DecalComponentData.h"
#include "ArgusLogging.h"
#include "ArgusStaticData.h"

void UDecalComponentData::InstantiateComponentForEntity(const ArgusEntity& entity) const
{
	DecalComponent* DecalComponentRef = entity.GetOrAddComponent<DecalComponent>();
	ARGUS_RETURN_ON_NULL(DecalComponentRef, ArgusECSLog);

	DecalComponentRef->m_lifetimeSeconds = m_lifetimeSeconds;
}

bool UDecalComponentData::MatchesType(UComponentData* other) const
{
	if (!other)
	{
		return false;
	}

	const UDecalComponentData* otherComponentData = Cast<UDecalComponentData>(other);
	if (!otherComponentData)
	{
		return false;
	}

	return true;
}
