#include "InputInterfaceComponentData.h"
#include "ArgusLogging.h"
#include "ArgusStaticData.h"

void UInputInterfaceComponentData::InstantiateComponentForEntity(ArgusEntity entity) const
{
	InputInterfaceComponent* InputInterfaceComponentRef = entity.GetOrAddComponent<InputInterfaceComponent>();
	ARGUS_RETURN_ON_NULL(InputInterfaceComponentRef, ArgusECSLog);

	InputInterfaceComponentRef->m_doubleClickThresholdSeconds = m_doubleClickThresholdSeconds;
	InputInterfaceComponentRef->m_doubleClickQueryRange = m_doubleClickQueryRange;
	InputInterfaceComponentRef->m_numControlGroups = m_numControlGroups;
}

bool UInputInterfaceComponentData::MatchesType(const UComponentData* other) const
{
	if (!other)
	{
		return false;
	}

	return other->IsA<UInputInterfaceComponentData>();
}
