void UPassengerComponentData::InstantiateComponentForEntity(ArgusEntity entity) const
{
	PassengerComponent* PassengerComponentRef = entity.GetOrAddComponent<PassengerComponent>();
	ARGUS_RETURN_ON_NULL(PassengerComponentRef, ArgusECSLog);

	PassengerComponentRef->m_carrierEntityId = m_carrierEntityId;
}

bool UPassengerComponentData::MatchesType(UComponentData* other) const
{
	if (!other)
	{
		return false;
	}

	const UPassengerComponentData* otherComponentData = Cast<UPassengerComponentData>(other);
	if (!otherComponentData)
	{
		return false;
	}

	return true;
}
