void USpatialPartitioningComponentData::InstantiateComponentForEntity(ArgusEntity entity) const
{
	SpatialPartitioningComponent* SpatialPartitioningComponentRef = entity.GetOrAddComponent<SpatialPartitioningComponent>();
	ARGUS_RETURN_ON_NULL(SpatialPartitioningComponentRef, ArgusECSLog);

	SpatialPartitioningComponentRef->m_validSpaceExtent = m_validSpaceExtent;
	SpatialPartitioningComponentRef->m_flyingPlaneHeight = m_flyingPlaneHeight;
	SpatialPartitioningComponentRef->m_elevatedObstaclePointHeightThreshold = m_elevatedObstaclePointHeightThreshold;
}

bool USpatialPartitioningComponentData::MatchesType(UComponentData* other) const
{
	if (!other)
	{
		return false;
	}

	const USpatialPartitioningComponentData* otherComponentData = Cast<USpatialPartitioningComponentData>(other);
	if (!otherComponentData)
	{
		return false;
	}

	return true;
}
