#include "TargetingComponentData.h"
#include "ArgusLogging.h"
#include "ArgusStaticData.h"

void UTargetingComponentData::InstantiateComponentForEntity(ArgusEntity& entity) const
{
	TargetingComponent* TargetingComponentRef = entity.AddComponent<TargetingComponent>();
	ARGUS_RETURN_ON_NULL(TargetingComponentRef, ArgusECSLog);

	TargetingComponentRef->m_meleeRange = m_meleeRange;
	TargetingComponentRef->m_rangedRange = m_rangedRange;
	TargetingComponentRef->m_sightRange = m_sightRange;
}

bool UTargetingComponentData::MatchesType(UComponentData* other) const
{
	if (!other)
	{
		return false;
	}

	const UTargetingComponentData* otherComponentData = Cast<UTargetingComponentData>(other);
	if (!otherComponentData)
	{
		return false;
	}

	return true;
}
