#include "TaskComponentData.h"
#include "ArgusLogging.h"
#include "ArgusStaticData.h"

void UTaskComponentData::InstantiateComponentForEntity(ArgusEntity entity) const
{
	TaskComponent* TaskComponentRef = entity.GetOrAddComponent<TaskComponent>();
	ARGUS_RETURN_ON_NULL(TaskComponentRef, ArgusECSLog);

	TaskComponentRef->m_baseState = m_baseState;
	TaskComponentRef->m_flightState = m_flightState;
}

void UTaskComponentData::OnComponentDataLoaded() const
{
}

bool UTaskComponentData::MatchesType(const UComponentData* other) const
{
	if (!other)
	{
		return false;
	}

	return other->IsA<UTaskComponentData>();
}
