#include "VelocityComponentData.h"
#include "ArgusLogging.h"
#include "ArgusStaticData.h"

void UVelocityComponentData::InstantiateComponentForEntity(const ArgusEntity& entity) const
{
	VelocityComponent* VelocityComponentRef = entity.GetOrAddComponent<VelocityComponent>();
	ARGUS_RETURN_ON_NULL(VelocityComponentRef, ArgusECSLog);

	VelocityComponentRef->m_desiredSpeedUnitsPerSecond = m_desiredSpeedUnitsPerSecond;
	VelocityComponentRef->m_desiredFlightSpeedUnitsPerSecond = m_desiredFlightSpeedUnitsPerSecond;
}

bool UVelocityComponentData::MatchesType(UComponentData* other) const
{
	if (!other)
	{
		return false;
	}

	const UVelocityComponentData* otherComponentData = Cast<UVelocityComponentData>(other);
	if (!otherComponentData)
	{
		return false;
	}

	return true;
}
