const UResourceSetRecord* UResourceSetRecordDatabase::GetRecord(uint32 id)
{
	ARGUS_MEMORY_TRACE(ArgusStaticData);

	if (static_cast<uint32>(m_UResourceSetRecordsPersistent.Num()) <= id)
	{
		ResizePersistentObjectPointerArray();
		
		if (static_cast<uint32>(m_UResourceSetRecordsPersistent.Num()) <= id)
		{
			ARGUS_LOG
			(
				ArgusStaticDataLog, Error,
				TEXT("[%s] Could not find %s %d in %s."),
				ARGUS_FUNCNAME,
				ARGUS_NAMEOF(id),
				id,
				ARGUS_NAMEOF(UResourceSetRecordDatabase)
			);

			return nullptr;
		}
	}

	if (id == 0u)
	{
		return nullptr;
	}

	if (!m_UResourceSetRecordsPersistent[id])
	{
		m_UResourceSetRecordsPersistent[id] = m_UResourceSetRecords[id].LoadSynchronous();
	}

	if (m_UResourceSetRecordsPersistent[id])
	{
		m_UResourceSetRecordsPersistent[id]->m_id = id;
	}

	return m_UResourceSetRecordsPersistent[id];
}

void UResourceSetRecordDatabase::ResizePersistentObjectPointerArray()
{
	ARGUS_MEMORY_TRACE(ArgusStaticData);

	m_UResourceSetRecordsPersistent.SetNumZeroed(m_UResourceSetRecords.Num());
}

#if WITH_EDITOR
void UResourceSetRecordDatabase::PreSave(FObjectPreSaveContext saveContext)
{
	FString fullPath = FPaths::ConvertRelativePathToFull(saveContext.GetTargetFilename());
	if (!std::filesystem::exists(TCHAR_TO_UTF8(*fullPath)))
	{
		TArray<FReferencerInformation> internalReferencers;
		TArray<FReferencerInformation> externalReferencers;
		RetrieveReferencers(&internalReferencers, &externalReferencers);

		if (internalReferencers.IsEmpty() && externalReferencers.IsEmpty())
		{
			ArgusStaticData::RegisterNewUResourceSetRecordDatabase(this);
		}
	}

	Super::PreSave(saveContext);
}

void UResourceSetRecordDatabase::PostEditChangeProperty(FPropertyChangedEvent& propertyChangedEvent)
{
	if (propertyChangedEvent.ChangeType != EPropertyChangeType::ValueSet)
	{
		return;
	}

	const FString propertyName = propertyChangedEvent.GetPropertyName().ToString();
	const FString recordPropertyName = ARGUS_NAMEOF(m_UResourceSetRecords);
	if (!propertyName.Equals(recordPropertyName))
	{
		return;
	}

	const int32 arrayIndex = propertyChangedEvent.GetArrayIndex(propertyName);
	UResourceSetRecord* modifiedUResourceSetRecord = m_UResourceSetRecords[arrayIndex].LoadSynchronous();
	if (!modifiedUResourceSetRecord)
	{
		return;
	}

	modifiedUResourceSetRecord->m_id = arrayIndex;

	if (!GEditor)
	{
		return;
	}

	UEditorAssetSubsystem* editorAssetSubsystem = GEditor->GetEditorSubsystem<UEditorAssetSubsystem>();
	if (!editorAssetSubsystem)
	{
		return;
	}

	editorAssetSubsystem->SaveLoadedAsset(modifiedUResourceSetRecord, false);
	editorAssetSubsystem->SaveLoadedAsset(this, false);
}

void UResourceSetRecordDatabase::AddUResourceSetRecordToDatabase(UResourceSetRecord* record)
{
	const int32 arrayIndex = m_UResourceSetRecords.Num();
	m_UResourceSetRecords.Add(TSoftObjectPtr(record));

	record->m_id = arrayIndex;

	if (!GEditor)
	{
		return;
	}

	UEditorAssetSubsystem* editorAssetSubsystem = GEditor->GetEditorSubsystem<UEditorAssetSubsystem>();
	if (!editorAssetSubsystem)
	{
		return;
	}

	editorAssetSubsystem->SaveLoadedAsset(this, false);
}
#endif //WITH_EDITOR
