#include "RecordReferences/ArgusActorRecordReference.h"
#include "ArgusLogging.h"

#if WITH_EDITOR && WITH_EDITORONLY_DATA
void FUArgusActorRecordReference::StoreId() const
{
	if (m_softObjectPtr.IsNull())
	{
		m_id = 0;
		return;
	}

	if (const UArgusActorRecord* record = m_softObjectPtr.LoadSynchronous())
	{
		m_id = record->m_id;
	}
	else
	{
		ARGUS_ERROR_NULL(ArgusStaticDataLog, record);
	}
}
#endif
