#include "ResourceSetRecordReference.h"
#include "ArgusLogging.h"
#include "RecordDefinitions/ResourceSetRecord.h"

#if WITH_EDITOR
void FResourceSetRecordReference::StoreId() const
{
	if (m_softObjectPtr.IsNull())
	{
		m_id = 0;
		return;
	}

	if (const UResourceSetRecord* resourceSetRecord = m_softObjectPtr.LoadSynchronous())
	{
		m_id = resourceSetRecord->m_id;
	}
	else
	{
		ARGUS_ERROR_NULL(ArgusStaticDataLog, resourceSetRecord);
	}
}
#endif
